import React from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";

const UnitCard = (props) => {
  const numberWithCommas = (x) => {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  return (
    <div className="card">
      <h3 className="card__title">{props.unit_description}</h3>
      <div className="card__header">
        <img
          className="card__header__photo"
          src=".../public/images/ocean-mansion.png"
          alt="House"
        />
      </div>
      <p className="card__location">to come</p>
      <div className="row">
        <div className="col-2-of-3">
          <p className="card__info">Balconies: ${props.num_of_balcony}</p>
          <p className="card__info">Bedrooms: {props.bedrooms}</p>
          <p className="card__info">Bathrooms: {props.num_of_bathrooms}</p>
        </div>
        <div className="col-1-of-3">
          <p className="card__startDate">
            {props.unit_availability_start_date}
          </p>
          <p className="card__enddate">{props.unit_availability_end_date}</p>
          <p className="card__sqft">
            Sqft: {numberWithCommas(props.living_area_sf)}
          </p>
        </div>
      </div>
    </div>
  );
};

UnitCard.propTypes = {
  num_of_bedrooms: PropTypes.number.isRequired,
  num_of_bathrooms: PropTypes.number.isRequired,
  num_of_balcony: PropTypes.number.isRequired,
  is_available: PropTypes.bool.isRequired,
  is_reserved: PropTypes.bool.isRequired,
  unit_availability_start_date: PropTypes.string.isRequired,
  unit_availability_end_date: PropTypes.string.isRequired,
  unit_description: PropTypes.string.isRequired,
  living_area_sf: PropTypes.number.isRequired,
  unit_number: PropTypes.number.isRequired,
  unit_at_floor: PropTypes.number.isRequired,
};

export default UnitCard;
