import React, { useState } from "react";
import {
  CCard,
  CCardImage,
  CCardBody,
  CCardTitle,
  CCardText,
  CListGroup,
  CListGroupItem,
  CCardLink,
  CCarButton,
} from "@coreui/react";

const UnitCard = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const togglePopup = () => {
    setIsOpen(!isOpen);
  };

  return (
    <CCard
      style={{ width: "45rem", height: "40rem" }}
      borderRadius="5rem"
      textColor="white"
      color="dark"
    >
      <CCardImage
        style={{ height: "15rem" }}
        orientation="bottom"
        src="/images/pool-by-sea.png"
      />
      <CCardBody textColor="white" color="dark">
        <CCardTitle>Unit ID Number: {props.id}</CCardTitle>
        <CCardText>Description: {props.unit_description}</CCardText>
      </CCardBody>
      <CCardBody>
        <CListGroupItem color="dark">Bedrooms: {props.bedrooms}</CListGroupItem>
        <CListGroupItem color="dark">
          Bathrooms: {props.num_of_bathrooms}
        </CListGroupItem>
        <CListGroupItem color="dark">
          Available Start Date: {props.unit_availability_start_date}
        </CListGroupItem>
        <CListGroupItem color="dark">
          Unit Sqft: {props.living_area_sf}
        </CListGroupItem>
      </CCardBody>
      <CCardBody textColor="white" color="dark">
        <CCardText>Monthly Rent: $ {props.price_per_month}</CCardText>
        <CCardButton onClick={togglePopup}>Details</CCardButton>
        <CCardLink href="/Contact">contact</CCardLink>
      </CCardBody>
    </CCard>
  );
};

export default UnitCard;
