import React, { useEffect, useState } from "react";
import UnitCard from "../components/UnitCard";
import axios from "axios";
import api from "../highOrder/axios";

export default function Explore() {
  const [units, setUnits] = useState([]);
  const [unit, setUnit] = useState("");

  const fetchUnit = async () => {
    api.get("unit/2/").then((res) => {
      setUnit(res.data.unit);
      console.log(unit);
    });
  };

  const fetchAllUnits = async () => {
    api.get("unitlisting/").then((res) => {
      setUnits([...units, res.data.units]);
      console.log(units);
    });
  };

  useEffect(() => {
    fetchUnit();
    fetchAllUnits();
  }, []);

  const displayListings = () => {
    let display = [];
    let result = [];

    units.map((unit) => {
      <UnitCard
        id={unit.id}
        num_of_bedrooms={unit.num_of_bedrooms}
        num_of_bathrooms={unit.num_of_bathrooms}
        num_of_balcony={unit.num_of_balcony}
        is_available={unit.is_available}
        is_reserved={unit.is_reserved}
        unit_availability_start_date={unit.unit_availability_start_date}
        unit_availability_end_date={unit.unit_availability_end_date}
        unit_description={unit.unit_description}
        living_area_sf={unit.living_area_sf}
        unit_number={unit.unit_number}
        unit_at_floor={unit.unit_at_floor}
      />;
    });

    for (let i = 0; i < units.length; i += 3) {
      result.push(
        <div key={i} className="row">
          <div className="col-1-of-3">{display[i]}</div>
          <div className="col-1-of-3">
            {display[i + 1] ? display[i + 1] : null}
          </div>
          <div className="col-1-of-3">
            {display[i + 2] ? display[i + 2] : null}
          </div>
        </div>
      );
    }

    return (
      <Container>
        <SearchWrap>
          <Header />
          {units.map((unit) => {
            <UnitCard
              id={unit.id}
              num_of_bedrooms={unit.num_of_bedrooms}
              num_of_bathrooms={unit.num_of_bathrooms}
              num_of_balcony={unit.num_of_balcony}
              is_available={unit.is_available}
              is_reserved={unit.is_reserved}
              unit_availability_start_date={unit.unit_availability_start_date}
              unit_availability_end_date={unit.unit_availability_end_date}
              unit_description={unit.unit_description}
              living_area_sf={unit.living_area_sf}
              unit_number={unit.unit_number}
              unit_at_floor={unit.unit_at_floor}
            />;
          })}
          <Footer />
        </SearchWrap>
      </Container>
    );
  };
}
