import React, { useState } from "react";
import axios from "axios";
import Loader from "react-loader-spinner";
import PropTypes from "prop-types";
import styled from "styled-components";
import api from "../highOrder/axios";

const UnitForm = () => {
  const [full, setFull] = useState(true);

  const [formData, setFormData] = useState({
    num_of_bedrooms: "0+",
    num_of_bathrooms: "0+",
    num_of_balcony: "0+",
    is_available: "true",
    is_reserved: "false",
    unit_availability_start_date: "",
    unit_availability_end_date: "",
    unit_description: "For Rent",
    living_area_sf: "0+",
    unit_number: "0+",
    unit_at_floor: "0+",
  });

  const {
    num_of_bedrooms,
    num_of_bathrooms,
    num_of_balcony,
    is_available,
    is_reserved,
    unit_availability_start_date,
    unit_availability_end_date,
    unit_description,
    living_area_sf,
    unit_number,
    unit_at_floor,
  } = formData;

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = (e) => async () => {
    const res = await api
      .post("unit/", formData)
      .then(console.log(res.data.form))
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      <Container2>
        <p fontSize="25px">
          Enter the Unit Information and lik submit. All fields are required
        </p>
      </Container2>
      <Wrap>
        <form>
          <Container>
            <div>
              <label htmlFor="num_of_bedrooms">Number of Bedrooms</label>
              <input
                type="number"
                name="num_of_bedrooms"
                min="0"
                max="20"
                onChange={(e) => onChange(e)}
                value={num_of_bedrooms}
              ></input>
            </div>

            <div>
              <label htmlFor="num_of_bathrooms">Number of Bathrooms</label>
              <input
                type="number"
                name="num_of_bathrooms"
                min="0"
                max="15"
                onChange={(e) => onChange(e)}
                value={num_of_bathrooms}
              ></input>
            </div>

            <div>
              <label htmlFor="num_of_balcony">Balcony Number</label>
              <input
                default="0"
                type="number"
                name="num_of_balcony"
                min="0"
                max="10"
                onChange={(e) => onChange(e)}
                value={num_of_balcony}
              ></input>
            </div>

            <div>
              <label htmlFor="is_available">Available?</label>
              <input
                type="checkbox"
                name="is_available"
                onChange={(e) => onChange(e)}
                value={is_available}
              ></input>
            </div>

            <div>
              <label htmlFor="is_reserved">Reserved?</label>
              <input
                type="checkbox"
                name="is_reserved"
                onChange={(e) => onChange(e)}
                value={is_reserved}
              ></input>
            </div>

            <div>
              <label htmlFor="unit_availability_start_date">
                Available Start Date:
              </label>
              <input
                type="date"
                name="unit_availability_start_date"
                onChange={(e) => onChange(e)}
                value={unit_availability_start_date}
              ></input>
            </div>

            <div>
              <label htmlFor="unit_availability_end_date">End Date</label>
              <input
                type="date"
                name="unit_availability_end_date"
                onChange={(e) => onChange(e)}
                value={unit_availability_end_date}
              ></input>
            </div>
          </Container>
          <Container>
            <div>
              <label htmlFor="unit_description">Unit Description</label>
              <textarea
                name="unit_description"
                rows="5"
                cols="40"
                wrap="soft"
                maxLength="1000"
                onChange={(e) => onChange(e)}
                value={unit_description}
              ></textarea>
            </div>

            <div>
              <label htmlFor="living_area_sf">Square Footage</label>
              <input
                type="text"
                name="living_area_sf"
                onChange={(e) => onChange(e)}
                value={living_area_sf}
              ></input>
            </div>

            <div>
              <label htmlFor="unit_number">Unit Number</label>
              <input
                type="text"
                name="unit_number"
                onChange={(e) => onChange(e)}
                value={unit_number}
              ></input>
            </div>

            <div>
              <label htmlFor="unit_at_floor">Floor (leave blank if none)</label>
              <input
                type="text"
                name="unit_at_floor"
                onChange={(e) => onChange(e)}
                value={unit_at_floor}
              ></input>
            </div>
            <div>
              <label></label>
              full?
              <FirstButton onClick={(e) => onSubmit(e)}>Submit</FirstButton>:
              <FirstButton opacity="0.50">Submit</FirstButton>
            </div>
          </Container>
        </form>
      </Wrap>
    </div>
  );
};

export default UnitForm;

const Wrap = styled.div`
  padding-top: 10%;
  padding-left: 400px;
  width: 100%;
  hight: 100%;
  float: left;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const FirstButton = styled.div`
  padding: 10px;
  width: 300px;
  color: #2577be;
  background-color: rgba(23, 26, 32, 0.8);
  height: 40px;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 60px;
  opacity: 0.85;
  text-transform: uppercase;
  font-size: 12px;
  cursor: pointer;
  text-align: center;
`;

const Container2 = styled.div`
  padding-top: 10px;
  width: 100%;
  height: 2vh;
  display: flex;
  align-items: center;
`;

const Container = styled.div`
  padding: 10px;
  padding-top: 10px;
  width: 50%;
  float: left;
  height: 100vh;
  align-items: column;
  label {
    width: 300px;
    color: #2577be;
    height: 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-transform: uppercase;
    font-size: 15px;
    text-align: center;
  }

  input {
    width: 300px;
    color: white;
    background-color: rgba(23, 26, 32, 0.8);
    height: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 60px;
    opacity: 0.85;
    font-size: 12px;
    cursor: hover;
    text-align: center;
  }

  select {
    width: 300px;
    color: white;
    background-color: rgba(23, 26, 32, 0.8);
    height: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 60px;
