import React, { useState } from "react";
import axios from "axios";
import Loader from "react-loader-spinner";
import PropTypes from "prop-types";
import styled from "styled-components";
import api from "../highOrder/axios";

const UnitForm = (props) => {
  const [formData, setFormData] = useState({
    num_of_bedrooms: "0+",
    num_of_bathrooms: "0+",
    num_of_balcony: "0+",
    is_available: "true",
    is_reserved: "false",
    unit_availability_start_date: "",
    unit_availability_end_date: "",
    unit_description: "For Rent",
    living_area_sf: "0+",
    unit_number: "0+",
    unit_at_floor: "0+",
  });

  const {
    num_of_bedrooms,
    num_of_bathrooms,
    num_of_balcony,
    is_available,
    is_reserved,
    unit_availability_start_date,
    unit_availability_end_date,
    unit_description,
    living_area_sf,
    unit_number,
    unit_at_floor,
  } = formData;

  const [loading, setLoading] = useState(false);

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = (e) => async () => {
    e.preventDefault();

    const config = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
    };

    setLoading(true);

    api
      .post(
        "unit/",
        {
          num_of_bedrooms,
          num_of_bathrooms,
          num_of_balcony,
          is_available,
          is_reserved,
          unit_availability_start_date,
          unit_availability_end_date,
          unit_description,
          living_area_sf,
          unit_number,
          unit_at_floor,
        },
        config
      )
      .then((res) => {
        setLoading(false);
        props.setUnit(res.data);
        window.scrollTo(0, 0);
      })
      .catch((err) => {
        setLoading(false);
        window.scrollTo(0, 0);
      });
  };

  return (
    <div>
      <Wrap>
        <form>
          <Container>
            <div>
              <label for="quantity">Number of Bedrooms</label>
              <input
                type="number"
                id="quantityBed"
                name="num_of_bedrooms"
                min="0"
                max="20"
                onChange={(e) => onChange(e)}
                value={num_of_bedrooms}
              ></input>
            </div>

            <div>
              <label for="quantity">Number of Bathrooms</label>
              <input
                type="number"
                id="quantityBath"
                name="num_of_bathrooms"
                min="0"
                max="15"
                onChange={(e) => onChange(e)}
                value={num_of_bathrooms}
              ></input>
            </div>

            <div>
              <label for="text">Balcony Number</label>
              <input
                default="0"
                type="number"
                id="quantityBalc"
                name="num_of_balcony"
                min="0"
                max="10"
                onChange={(e) => onChange(e)}
                value={num_of_balcony}
              ></input>
            </div>

            <div>
              <label for="quantity">Available?</label>
              <input
                type="checkbox"
                name="is_available"
                onChange={(e) => onChange(e)}
                value={is_available}
              ></input>
            </div>

            <div>
              <label for="quantity">Reserved?</label>
              <input
                type="checkbox"
                name="is_reserved"
                onChange={(e) => onChange(e)}
                value={is_reserved}
              ></input>
            </div>

            <div>
              <label for="birthday">Available Start Date:</label>
              <input
                type="date"
                id="startDate"
                name="unit_availability_start_date"
                onChange={(e) => onChange(e)}
                value={unit_availability_start_date}
              ></input>
            </div>

            <div>
              <label for="birthday">End Date</label>
              <input
                type="date"
                id="endDate"
                name="unit_availability_end_date"
                onChange={(e) => onChange(e)}
                value={unit_availability_end_date}
              ></input>
            </div>
          </Container>
          <Container>
            <div>
              <label for="description">Unit Description</label>
              <textarea
                name="unit_description"
                rows="7"
                cols="40"
                wrap="soft"
                maxlength="1000"
                onChange={(e) => onChange(e)}
                value={unit_description}
              ></textarea>
            </div>

            <div>
              <label for="area" class="form-label">
                Square Footage
              </label>
              <input
                type="text"
                class="form-control"
                id="area"
                name="living_area_sf"
                onChange={(e) => onChange(e)}
                value={living_area_sf}
              ></input>
            </div>

            <div>
              <label for="unitNumber" class="form-label">
                Unit Number
              </label>
              <input
                type="text"
                class="form-control"
                id="unitNumber"
                name="unit_number"
                onChange={(e) => onChange(e)}
                value={unit_number}
              ></input>
            </div>

            <div>
              <label for="floor" class="form-label">
                Floor (leave blank if none)
              </label>
              <input
                type="text"
                class="form-control"
                id="floor"
                name="unit_at_floor"
                onChange={(e) => onChange(e)}
                value={unit_at_floor}
              ></input>
            </div>
            <div>
              <FirstButton onClick={(e) => onSubmit(e)}>Submit</FirstButton>
            </div>
          </Container>
          <div>
            <div class="form-check"></div>
          </div>
        </form>
      </Wrap>
    </div>
  );
};

UnitForm.propTypes = {
  setListings: PropTypes.func.isRequired,
};

export default UnitForm;

const Wrap = styled.div`
  padding-top: 10%;
  padding-left: 300px;
  width: 100%;
  hight: 100%;
  float: left;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const FirstButton = styled.div`
  width: 500px;
  color: #2577be;
  background-color: rgba(23, 26, 32, 0.8);
  height: 40px;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 60px;
  opacity: 0.85;
  text-transform: uppercase;
  font-size: 12px;
  cursor: pointer;
  text-align: center;
`;

const Container = styled.div`
  width: 50%;
  float: left;
  height: 100vh;
  align-items: column;
  label {
    width: 300px;
    color: #2577be;
    height: 20px;
