import React, { useState } from "react";
import axios from "axios";
import Loader from "react-loader-spinner";
import PropTypes from "prop-types";
import styled from "styled-components";
import api from "../highOrder/axios";
import $ from "jquery";
import jQuery from "jquery";

const UnitForm = () => {
  jQuery(function($) {
    var $fields = $(
      "#num_of_bedrooms, #num_of_bathrooms, #num_of_balcony, #is_available, #is_reserved, #unit_availability_start_date, #unit_availability_end_date, #unit_description, #living_area_sf, #unit_number, #unit_at_floor"
    );

    $fields.on("keyup change", function() {
      if (allFilled($fields)) {
        $("#submit").removeAttr("disabled");
      }
    });

    function allFilled($fields) {
      return (
        $fields.filter(function() {
          return this.value === "";
        }).length == 0
      );
    }
  });

  const [formData, setFormData] = useState({
    num_of_bedrooms: "0",
    num_of_bathrooms: "0",
    num_of_balcony: "0",
    is_available: "true",
    is_reserved: "false",
    unit_availability_start_date: "",
    unit_availability_end_date: "",
    unit_description: "For Rent",
    living_area_sf: "0",
    unit_number: "0",
    unit_at_floor: "0",
  });

  const {
    num_of_bedrooms,
    num_of_bathrooms,
    num_of_balcony,
    is_available,
    is_reserved,
    unit_availability_start_date,
    unit_availability_end_date,
    unit_description,
    living_area_sf,
    unit_number,
    unit_at_floor,
  } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    console.log(formData);
  };

  const onSubmit = () => async () => {
    const res = await axios
      .post("http://127.0.0.1:8000/unit/", {
        id: 2,
        num_of_bedrooms: 2,
        num_of_bathrooms: 2,
        num_of_balcony: 2,
        is_available: true,
        is_reserved: false,
        unit_availability_start_date: null,
        unit_availability_end_date: null,
        unit_description: "create place man",
        living_area_sf: 5000,
        unit_number: 23,
        unit_at_floor: 2,
      })
      .then(console.log(res))
      .catch((err) => {
        console.log({
          num_of_bedrooms,
          num_of_bathrooms,
          num_of_balcony,
          is_available,
          is_reserved,
          unit_availability_start_date,
          unit_availability_end_date,
          unit_description,
          living_area_sf,
          unit_number,
          unit_at_floor,
        });
        console.log(res.data.data);
        console.log(err);
      });
  };

  return (
    <div>
      <Container2>
        <p fontSize="25px">
          Enter the Unit Information and lik submit. All fields are required
        </p>
      </Container2>
      <Wrap>
        <form>
          <Container>
            <div>
              <label htmlFor="num_of_bedrooms">Number of Bedrooms</label>
              <input
                id="num_of_bedrooms"
                type="number"
                name="num_of_bedrooms"
                min="0"
                max="20"
                onChange={(e) => onChange(e)}
                value={num_of_bedrooms}
              ></input>
            </div>

            <div>
              <label htmlFor="num_of_bathrooms">Number of Bathrooms</label>
              <input
                id="num_of_bathrooms"
                type="number"
                name="num_of_bathrooms"
                min="0"
                max="15"
                onChange={(e) => onChange(e)}
                value={num_of_bathrooms}
              ></input>
            </div>

            <div>
              <label htmlFor="num_of_balcony">Balcony Number</label>
              <input
                id="num_of_balcony"
                default="0"
                type="number"
                name="num_of_balcony"
                min="0"
                max="10"
                onChange={(e) => onChange(e)}
                value={num_of_balcony}
              ></input>
            </div>

            <div>
              <label htmlFor="is_available">Available?</label>
              <input
                id="is_available"
                type="checkbox"
                name="is_available"
                onChange={(e) => onChange(e)}
                value={is_available}
              ></input>
            </div>

            <div>
              <label htmlFor="is_reserved">Reserved?</label>
              <input
                id="is_reserved"
                type="checkbox"
                name="is_reserved"
                onChange={(e) => onChange(e)}
                value={is_reserved}
              ></input>
            </div>

            <div>
              <label htmlFor="unit_availability_start_date">
                Available Start Date:
              </label>
              <input
                id="unit_availability_start_date"
                type="date"
                name="unit_availability_start_date"
                onChange={(e) => onChange(e)}
                value={unit_availability_start_date}
              ></input>
            </div>

            <div>
              <label htmlFor="unit_availability_end_date">End Date</label>
              <input
                id="unit_availability_end_date"
                type="date"
                name="unit_availability_end_date"
                onChange={(e) => onChange(e)}
                value={unit_availability_end_date}
              ></input>
            </div>
          </Container>
          <Container>
            <div>
              <label htmlFor="unit_description">Unit Description</label>
              <textarea
                id="unit_description"
                name="unit_description"
                rows="5"
                cols="40"
                wrap="soft"
                maxLength="1000"
                onChange={(e) => onChange(e)}
                value={unit_description}
              ></textarea>
            </div>

            <div>
              <label htmlFor="living_area_sf">Square Footage</label>
              <input
                id="living_area_sf"
                type="text"
                name="living_area_sf"
                onChange={(e) => onChange(e)}
                value={living_area_sf}
              ></input>
            </div>

            <div>
              <label htmlFor="unit_number">Unit Number</label>
              <input
                id="unit_number"
                type="text"
                name="unit_number"
                onChange={(e) => onChange(e)}
                value={unit_number}
              ></input>
            </div>

            <div>
              <label htmlFor="unit_at_floor">Floor (leave blank if none)</label>
              <input
                id="unit_at_floor"
                type="text"
                name="unit_at_floor"
                onChange={(e) => onChange(e)}
                value={unit_at_floor}
              ></input>
            </div>
            <div>
              <label></label>
              <FirstButton
                type="submit"
                name="Submit"
                id="submit"
                disabled="disabled"
                onClick={onSubmit()}
              >
                Submit
              </FirstButton>
            </div>
          </Container>
        </form>
      </Wrap>
    </div>
  );
};

