import React, { useState, onClick } from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import Header from "../components/Header";
import Footer from "../components/Footer";

function EnterData() {
  const [submit, setSubmit] = useState(true);

  const handleSubmit = (a) => {
    setSubmit(a);
  };

  return (
    <Container>
      <Header />
      <ItemText>
        <SearchWrap>
          {submit ? (
            <ButtonGroup>
              <form>
                <div>
                  <label for="quantity">Quantity (between 1 and 5):</label>
                  <input
                    type="number"
                    id="quantity"
                    name="quantity"
                    min="1"
                    max="5"
                  ></input>
                </div>

                <div>
                  <label for="inputAddress" class="form-label">
                    Address
                  </label>
                  <input
                    type="text"
                    class="form-control"
                    id="inputAddress"
                    placeholder="1234 Main St"
                  ></input>
                </div>
                <div>
                  <label for="inputAddress2" class="form-label">
                    Address 2
                  </label>
                  <input
                    type="text"
                    class="form-control"
                    id="inputAddress2"
                    placeholder="Apartment, studio, or floor"
                  ></input>
                </div>
                <div>
                  <label for="inputCity" class="form-label">
                    City
                  </label>
                  <input
                    type="text"
                    class="form-control"
                    id="inputCity"
                  ></input>
                </div>
                <div>
                  <label for="inputState" class="form-label">
                    State
                  </label>
                  <select id="inputState" class="form-select">
                    <option selected>Choose...</option>
                    <option value={1}>Connecticut</option>
                    <option value={2}>Delaware</option>
                    <option value={3}>Maine</option>
                    <option value={4}>Maryland</option>
                    <option value={5}>Massachusetts</option>
                    <option value={6}>New Hampshire</option>
                    <option value={1}>Vermont</option>
                  </select>
                </div>
                <div>
                  <label for="inputZip" class="form-label">
                    Zip
                  </label>
                  <input type="text" class="form-control" id="inputZip"></input>
                </div>
                <div>
                  <div class="form-check"></div>
                </div>
                <div>
                  <Button onClick={() => handleSubmit(false)}>Submit</Button>
                </div>
              </form>
            </ButtonGroup>
          ) : (
            /* Resulting search */
            <ButtonGroup>
              <Button onClick={() => handleSubmit(true)}>Back to Search</Button>
            </ButtonGroup>
          )
          /*End of search results*/
          }
        </SearchWrap>
      </ItemText>
      <Footer />
    </Container>
  );
}

export default EnterData;

const SearchWrap = styled.div`
  width: 100%;
  height: 100%;
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  background-image: url("/images/pool-by-sea.png");
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
`;

const Button = styled.div`
  type: submit;
  class: btn btn-primary
  width: 100px;
    color: white;
    background-color: rgba(18, 20, 34, 0.8);
    height: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 60px;
    opacity: 0.95;
    text-transform: uppercase;
    font-size: 18px;
    cursor: pointer;
    text-align: center;
`;

const Container = styled.div`
  height: 100vh;
  label {
    width: 300px;
    color: purple;
    height: 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-transform: uppercase;
    font-size: 15px;
    text-align: center;
  }

  input {
    width: 300px;
    color: white;
    background-color: rgba(23, 26, 32, 0.8);
    height: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 60px;
    opacity: 0.85;
    font-size: 12px;
    cursor: hover;
    text-align: center;
  }

  select {
    width: 300px;
    color: white;
    background-color: rgba(23, 26, 32, 0.8);
    height: 40px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 60px;
    opacity: 0.85;
    font-size: 15px;
    cursor: pointer;
    text-align: center;
  }
`;

const ItemText = styled.div`
  padding-top: 15vh;
  color: blue;
  text-align: center;
  display: flex;
`;

const ButtonGroup = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  margin-bottom: 50px;
  @media (max-width: 1000px) {
    flex-direction: column;
  }
  justify-content: center;
`;
const FirstButton = styled.div`
  width: 300px;
  color: white;
  background-color: rgba(23, 26, 32, 0.8);
  height: 40px;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 60px;
  opacity: 0.85;
  text-transform: uppercase;
  font-size: 12px;
  cursor: pointer;
  text-align: center;
`;
